basicnumber.script:
local rollwidget;
function onInit()
	rollwidget = addBitmapWidget("indicator_dice");
	if rollwidget then

			rollwidget.setPosition("insidetopright", -5, 2);
			rollwidget.setSize(20,20);
			rollwidget.setColor("AAFFFFFF");
	end
end

function action(draginfo)
	local rActor = ActorManager.getActor("pc", window.getDatabaseNode());
	ActionSkill.performRoll(draginfo, rActor,type[1],label[1],getValue(),nil,false);

	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)	
	return action();
end

