abilitydata = {
    --Prerequisites are entered 2 times once as text that is displayed for the ability and the second time as data structure for prerequisite checks like statprereq={["statname"]=minimumlevel,["statname"]=minimumlevel} same for skills just with skillprereq, for abilities its simply a list of required abilities abilityprereq={"ability1","ability2"}
	--shortdesc is for later use, when there will be a table in a module with all the abilities I didn't enter it for the last few abilities
	["Huge Stature"] = { order=1, type="Ogrun", prereq="None", desc="An ogrun can wield a weapon in one hand that usually requires two hands to wield, but he suffers -2 on attack rolls with that weapon.", shortdesc="Wield two handed weapon in one hand, suffer -2 to attack"},
    ["Gifted"] = { order=10, type="Gifted", prereq="None", desc="A character with the Gifted archetype starts with a tradition(p. 228). If the character begins the game with the warcaster career, he is a focuser; otherwise, he is a will weaver. Choosing the Gifted archetype is the only way for a character to have an ARC stat. If the character is a focuser, he starts the game with ARC 2. Will weavers start the game with ARC 3", shortdesc="Get ARC 3 or ARC 2", starting=true},
    ["Additional Study"] = { order=11, type="Gifted", prereq="None", desc="The character delves further into the mysteries of the arcane and is rewarded with a spell from one of his career spell lists- This benefit can be taken multiple times, but a character still cannot exceed twice his INT in spells known.", shortdesc="+1 spell", multiability=true },
    ["Combat Caster"] = { order=12, type="Gifted", prereq="None", desc="When this character makes a magic attack roll, he gains an additional die. Discard the lowest die of each roll.", shortdesc="+1 die on magic attacks, discard lowest" },
    ["Fast Caster"] = { order=13, type="Gifted", prereq="None", desc="The character gains one extra quick action each activation that can be used only to cast a spell.", shortdesc="+1 quicj action/activation for spellcasting." },
    ["Feat: Dominator"] = { order=14, type="Gifted", prereq="None", desc="The character can spend 1 feat point during his turn to double his control area for 1 turn.", shortdesc="Spend 1 feat point to double control area for 1 turn" },
    ["Feat: Powerful Caster"] = { order=15, type="Gifted", prereq="None", desc="The character can spend 1 feat point when he casts a spell to increase the RNG(range) of the spell by twelve feet(2 inches). Spells with a range of CTRL(control area) or SP(spray attack) are not affected.", shortdesc="Spend 1 feat point to increase targeted spell range by 2" },
    ["Feat: Quick Cast"] = { order=16, type="Gifted", prereq="None", desc="The character can spend 1 feat point to immediately cast one upkeep spell at the start of combat before the first round. When casting a spell as a result of this benefit, the character is not required to pay the COST of the spell,", shortdesc="Spend 1 feat point to immediately cast an upkeep spell before combat for free"},
    ["Feat: Strength of Will"] = { order=17, type="Gifted", prereq="None", desc="After failing a fatigue roll, the character can spend 1 feat point to instead automatically suceed on the roll. This benefit can only be taken by characters with the will weaver tradition.", shortdesc="Spend 1 feat point to automatically succeed a failed fatigue roll" },
    ["Magic Sensitivity"] = { order=18, type="Gifted", prereq="None", desc="The character can automatically sense when an another character casts a spell within fifty feet for each point of his ARC stat. Such characters can tune out this detection as background noise but are aware of particularly powerful magic. Additionally, a character with the focuser tradition can sense other focusers within their detection range.", shortdesc="Sense magic within ARC*50 feet" },
    ["Rune Reader"] = { order = 19, type="Gifted", prereq="None", desc="The character can identify any spell cast in his line of sight by reading the accompanying spell runes(see the 'Runes and Formulae' sidebar, p. 228). He can also learn the type of magic cast(the spell list it came from) and the tradition of the character casting the spell.", shortdesc="Identify spells cast in line of sight"},
    ["Warding Circle"] = {order = 20, type="Gifted", prereq="None", desc="The character can spend fifteen minutes to create a circle of warding runes around a small room or campsite. The names of the characters he intends to keep safe within the circle are incorporated into the runes. When any other character enters the circle, all named characters are alerted. While in the circle, non-named characters lose incorporeal, and non-named undead and infernal characters suffer -2 on attack rolls.", shortdesc="15 minutes to create circle. Alerts, cancels incorporeal and gives infernals and undead -2 to attack"},
    
    ["Intellectual"] = {order = 21, type="Intellectual", prereq="None", desc="Intellectual characters possess a tactical genius and adaptability that gives them +1 on attack and damage rolls in combat. While in the command range of an Intellectual character, a friendly character listening to his orders also gains +1 on his attack and damage rolls. This bonus is non-cumulative, so a character can gain this bonus only from one intellectual character at a time.", shortdesc="+1 attack and damage in command range", starting=true},
    ["Battlefield Coordination"] = {order = 22, type="Intellectual", prereq="None", desc="The character is a skilled battlefield commander. He is able to coordinate the movement and attacks of friendly forces to maximum effect. While in his command range, friendly characters do not suffer the firing into melee penalty for ranged attacks and spells and do not have a chance to hit friendly characters when they miss with ranged or magic attacks while firing into melee.", shortdesc="No friendly fire and firing into melee penalty in command range" },
    ["Feat: Flawless Timing"] = {order=23, type="Intellectual", prereq="None", desc="The character can spend 1 feat point to use this benefit during his turn. When he uses Flawless Timing, the character names an enemy. The next time that enemy directly hits him with an attack that encounter, the attack is instead considered to be a miss.", shortdesc="Next hit from named enemy is a miss instead" },
    ["Feat: Prescient"] = {order=24, type="Intellectual", prereq="None", desc="The character can spend 1 feat point to win initiative automatically and take the first turn that combat. If two or more characters use this ability, they make initiative rolls to determine which of them goes first.", shortdesc="Spend 1 feat point to automatically win initiative at the start of combat"},
    ["Feat: Perfect Plot"] = {order=25, type="Intellectual", prereq="None", desc="The character is a flawless planer and allows nothing to escape his attention. Assuming he is able to oversee all aspects of his plan, scout out the related sites, and do his research in great detail, he is sure to succeed. Of course this degree of planning takes time and care, but perfection is not without its cost. The character must spend 1 feat point to use this ability. A character following this character's plan gains an additional die on non-combat related rolls during the day in which the plan was enacted.", shortdesc="Spend 1 feat point and make a plan, characters following the plan gain +1 die on non-combat rolls that day"},
    ["Feat: Plan of Action"] = {order=26, type="Intellectual", prereq="None", desc="At the start of combat, the character can spend 1 feat point to use this benefit. During that combat, he and friendly characters who follow his plan gain +2 to their initiative rolls and +2 to their attack rolls during the first round of combat.", shortdesc="Spend 1 feat point, the character and his allies gain +2 initiative and attack in the first round"},
    ["Feat: Quick Thinking"] = {order=27, type="Intellectual", prereq="None", desc="The character's quick thinking enables him to act impossibly fast. Once per round the character can spend 1 feat point to make one attack or quick action at the start of another character's turn.", shortdesc="Spend 1 feat point to make 1 attack or quick action at the start of another character's turn"},
    ["Feat: Unconventional Warfare"] = { order=28, type="Intellectual", prereq="None", desc="The character is quick thinking enough to assess any situation, see every potential angle and outcome, and use the environment itself as a weapon. He can use his attacks to off-balance foes and send them careening off ledges or into nearby vats of molten metal, cause them to stumble over terrain features, hit their weak spots to knock them to the ground, or otherwise maneuver them into a position of weakness and jeopardy. The character must spend 1 feat point to use this ability and explain to the Game Master how he is turning the environment against his enemy. The Game Master then determines the likely effect the character's action or attack. Outcomes include a boosted damage roll(see p. 197), knockdown, push, slam, or a fall from a height.", shortdesc="Spend 1 feat point to get an additional effect for the attack ie: boosted damage, knock-down, push, slam or falling"},
    ["Genius"]={order=29, type="Intellectual", prereq="None", desc="The character possesses an incredible aptitude for intellectual pursuits. The character's INT rolls are boosted.", shortdesc="INT rolls are boosted"},
    ["Hyper Perception"] = {order=30, type="Intellectual", prereq="None", desc="The character's keen senses miss few details. The character's PER rolls are boosted.", shortdesc="PER rolls are boosted"},
    ["Photographic Memory"] = {order=31, type="Intellectual", prereq="None", desc="The character has a photographic memory and can recall every event in perfect detail. During play he can call upon his memory to ask the Game Master questions portaining anything he has seen or experienced.", shortdesc="Character has photographic memory"},
    
    ["Mighty"] = {order=32, type="Mighty", prereq="None", desc="Mighty characters gain an additional die on their melee damage rolls.", shortdesc="+1 die on melee damage rolls", starting=true},
    ["Beat Back"] ={order=33, type="Mighty", prereq="None", desc="When this character hits a target with a melee attack, he can immediately push his target 1 inch directly away. After the target is pushed, this character can advance up to 1 inch.", shortdesc="Push target 1 inch and advance 1" },
    ["Feat: Back Swing"] = { order=34, type="Mighty", prereq="None", desc="Once per turn, this character can spend 1 feat point to gain one additional melee attack.", shortdesc="Spend 1 feat point to gain +1 melee attack" },
    ["Feat: Bounding Leap"] ={order=35, type="Mighty", prereq="None", desc="The character is capable of preternatural feats of athleticism. Once during each of his turns in wich the character does not run or charge, he can spend 1 feat point to pitch himself over the heads of his enemies into the heart of battle. When the character uses this benefit, place him anywhere within 5inches of his current location.", shortdesc="Place character anywhere within 5 inches"},
    ["Feat: Counter Charge"] ={order=36, type="Mighty", prereq="None", desc="When an enemy advances and ends it movement within 36 feet(6 inches) of this character and in his line of sight, this character can immediately spend 1 feat point to charge the enemy. The character cannot make a counter charge while engaged.", shortdesc="When enemy stops its advance within 6 inches in line of sight spend 1 feat point to charge the enemy" },
    ["Feat: Invulnerable"] = {order=37, type="Mighty", prereq="None", desc="The character can spend 1 feat point during his turn to gain +3 ARM for one round.", shortdesc="Spend 1 feat point to get +3 ARM for 1 round" },
    ["Feat: Revitalize"] = {order=38, type="Mighty", prereq="None", desc="The character can spend 1 feat point during his turn to regain a number of vitality points equal to his PHY stat immediately. If a character suffers damage during his turn the damage must be resolved before a character can use this feat. An incapacitated character cannot use Revitalize.", shortdesc="Spend 1 feat point to regain PHY vitality"},
    ["Feat: Shield Breaker"]={order=39, type="Mighty", prereq="None", desc="When this character hits a target that has a shield with a melee attack, the character can spend 1 feat point to use this benefit. When the character uses this benefit after damage has been dealt the other character's shield is completely destroyed as a result of the attack.", shortdesc="Spend 1 feat point after hit to destroy shield" },
    ["Feat: Vendetta"]={order=40, type="Mighty", prereq="None", desc="The character can spend 1 feat point during his turn to use this benefit. When this ability is used the character names one enemy. For the rest of the encounter this character gains boosted attack rolls against that enemy. A character can use this benefit only once per encounter unless the original subject of his vendetta is destroyed, at wich point the character can spend a feat point to use this benefit again.", shortdesc="Spend 1 feat point to gain boosted attacks against target for the encounter"},
    ["Righteous Anger"]={order=41, type="Mighty", prereq="None", desc="When one or more characters who are friendly to this character are damaged by an enemy attack while in this character's command range, this character gains +2 STR and ARM for one round.", shortdesc="When an ally is damaged while inside the characters command range gain +2 ARM and STR for 1 round"},
    ["Tough"]={order=42, type="Mighty", prereq="None", desc="The character is incredibly hardy. When this character is disabled, roll 1d6. On a 5 or 6, the character heals 1 vitality point, is no longer disabled, and is knocked down.", shortdesc="When disabled roll 1d6: On 5 or 6 heal 1 vitality, the character is knocked down not disabled"},
    
    ["Skilled"]={order=43, type="Skilled", prereq="None",desc="A Skilled character gains an additional attack during his Activation Phase if he chooses to attack that turn.", shortdesc="+1 attack/activation", starting=true},
    ["Ambidextrous"]={order=44,type="Skilled",prereq="None",desc="The character does not suffer the normal attack roll penalty with a second weapon while using the Two.Weapon Fighing ability.", shortdesc="Use Two-Weapon Fighting with no penalty"},
    ["Cagey"]={order=45,type="Skilled", prereq="None", desc="When this character becomes knocked down, he can immediately move up tp twelve feet(2 inches) and cannot be targeted by free strikes during this movement. This benefit has no effect while the character is mointed. While knocked down, this character is not automatically hit by melee attacks and his DEF is not reduced. The character can stand up during his turn without forfeiting his movement or action.", shortdesc="Move 2 incehs on knock down, melee attacks dont auto hit, no DEF penalty, can stand up without losing movement or action"},
    ["Deft"]={order=46, type="Skilled", prereq="None", desc="The character has nimble fingers and steady hands. The character gains boosted AGI rolls.", shortdesc="AGI rolls are boosted"},
    ["Feat: Defensive Strike"] = { order=47, type="Skilled", prereq="None", desc="When an enemy advances into and ends its movement in this character's melee range, this character can spend 1 feat point to immediately make one melee attack targeting it.", shortdesc="Spend 1 feat point to gain a first strike" },
    ["Feat: Disarm"]={order=48, type="Skilled", prereq="None", desc="After directly hitting an enemy with non-spray, non-AOE(area of effect) ranged or melee attack, instead of making a damage roll, the character can spend 1 feat point to disarm his opponent. When this benefit is used, the enemy's weapon, or any object in his hand, flies from his grasp. He suffers no damage from this attack.", shortdesc="Spend 1 feat point to disarm enemy instead of damage"},
    ["Feat: Swashbuckler"]={order=49, type="Skilled", prereq="None", desc="Once during each of his turns, this character can spend 1 feat point to use Swashbuckler. The next time this character makes an attack with a hand weapon after using this benefit, his ront arc extends to 360 degrees, and he can make one melee attack against each enemy in his line of sight in his melee range. Regardless of the number of characters hit, Swashbuckler can trigger the Sidestep benefit only once.", shortdesc="Attack all adjacent characters"},
    ["Feat: Untouchable"] ={order=50, type="Skilled", prereq="None", desc="The character can spend 1 feat point during his turn to gain +3 DEF for one round.", shortdesc="Spend 1 feat point to gain +3 DEF for 1 round"},
    ["Preternatural Awareness"] ={order=51, type="Skilled", prereq="", desc="The character's uncanny perception keeps him constantly aware of his surroundings. The character gains boosted initiative rolls. Additionally, enemies never gain back strike bonuses against this character.", shortdesc="Initiative is boosted, immune to back strike"},
    ["Sidestep"]={order=52, type="Skilled", prereq="None", desc="When this character hits an enemy character with a melee weapon, he can advance up tp 2 inches after the attack is resolved. This character cannot be targeted by free strikes during this movement.", shortdesc="Advance 2 inches after hit, no free strikes"},
    ["Virtuoso"]={order=53, type="Skilled", prereq="None", desc="Choose a military skill. When making a non-AOE attack with a weapon that uses that skill, this character gains an additional die on his attack and damage rolls. Discard the lowest die for each roll. This benefit can be taken more than once, each time speciying a different military skill.", shortdesc="Gain +1 die on attack and damage rolls with chosen weapon skill, discard lowest for both", multiability=true},
	
	["'Jack Marshal"] = {order=54, type="Ability", prereq="None", desc="The character is skilled at instructing steamjacks to act. The 'jack marshal must know the cortex's native language to instruct it as well as the 'jack's cortex codes.\r\rBonded steamjacks (p. 323) cannot be affected by this ability.\r\rFor a complete description of how this ability is used see 'Jack Marshals' (p. 322).", shortdesc=""},
	["Ace Commander"] = {order=55,  type="Ability", prereq="'Jack Marshal, Command 2",  abilityprereq={"'Jack Marshal"}, skillprereq={["Command"]=2}, desc="The character gains an extra quick action during each of his turns that can only be used to drive a 'jack", shortdesc="" },
	["Acrobatics"] ={order = 56, type="Ability", prereq="Agility 6", statprereq={["Agility"]=6}, desc="The character can advance through other characters if he has enough movement to move completely past their bases. The character also gains +3 on his jumping skill rolls.", shortdesc=""},
	["Advisor"]={order=57, type="Ability", prereq="Command 2", skillprereq={["Command"]=2}, desc="While B2B(base-to-base see p. 204) with this character, friendly characters gain +1 to their command range.", shortdesc=""},
	["Ambush"]={order=58, type="Ability", prereq="None", desc="During the first round of an encounter, this character gains boosted attack and damage rolls against enemies that have not yet activated that encounter.", shortdesc="" },
	["Anatomical Precision"]={order=59, type="Ability", prereq="None",  desc="When this character hits a living target with a melee attack but the damage roll fails to exceed the target's ARM, the target suffers d3 damage points instead of the damage rolled.", shortdesc=""},
	["Appraise"]={order=60, type="Ability", prereq="None", desc="The character has a sharp eye and keen mind for detail, especially where monetary values are concerned. The character can judge the value of most fine goods with an inspection. Truly good fakes might require a Detection + INT roll to spot.", shortdesc=""},
	["Arcane Assassin"]={order=61, type="Ability", prereq="None", desc="When making attacks, this character ignores focus points overboosting the target's Power Field and spell effects adding to its ARM or DEF.", shortdesc=""},
	["Arcane Defenses"]={order=62, type="Ability", prereq="Arcane 5", statprereq={["Arcane"]=5}, desc="This character gains +3 ARM against magic attacks.", shortdesc=""},
	["Arcane Engineer"]={order=63, type="Ability", prereq="Mechanikal Eng 2", skillprereq={["Mechanikal Eng"]=2}, desc="This character can reroll failed Mechanikal Engineering rolls. Each failed roll can be rerolled only once as a result of Arcane Engineer.", shortdesc="" },
	["Arcane Precision"]={order=64, type="Ability", prereq="Detection 3", skillprereq={["Detection"]=3}, desc="When this character forfeits his movement to aim with a ranged weapon, he ignores stealth that turn.", shortdesc=""},
	["Arcane Scholar"]={order=65, type="Ability", prereq="None", desc="This character can have a number of spells equal to his INT x3, instead of the normal limit of INT x2.", shortdesc=""},
	["Astute"]={order=66, type="Ability", prereq="Detection 1", skillprereq={["Detection"]=1}, desc="The character can reroll failed Detection rolls. Each failed roll can be rerolled only once as a result of Astute.", shortdesc=""},
	["Backstab"]={order=67, type="Ability", prereq="None", desc="This character gains an additional die on his back strike damage rolls.", shortdesc=""},
	["Battle Commander"]={order=68, type="Ability", prereq="Command 3", skillprereq={["Command"]=3}, desc="This character can use one battle plan during each of his turns without spending a feat point.", shortdesc=""},
	["Battle Plan: Call to Action"]={order=69, type="Ability", prereq="Command 1", skillprereq={["Command"]=1}, desc="The character can spend 1 feat point to use Battle Plan: Call to Action. Using a battle plan is a quick action. When a character uses this battle plan, each friendly character who is in his command range who is under his command and is knocked down immediately stands up or goes prone.", shortdesc=""},
	["Battle Plan: Coordinated Strike"]={order=70, type="Ability", prereq="Command 1", skillprereq={["Command"]=1}, desc="The character can spend 1 feat point to use Battle Plan: Coordinated Strike during a surprise round (p. 201) before a battle. Using a battle plan is a quick action. When a character uses a battle plan, each friendly character in his command range can immediately make one attack. After these attacks, the surprise round ends and the characters are detected.", shortdesc=""},
	["Battle Plan: Desperate Pace"]={order=71, type="Ability", prereq="Command 3", skillprereq={["Command"]=3}, desc="The character can spend 1 feat point to use Battle Plan: Desperate Pace. Using a battle plan is a quick action. When a character uses this battle plan, each friendly character who follows the character's orders gains +2\" movement for one round.", shortdesc=""},
	["Battle Plan: Go to Ground"]={order=72, type="Ability", prereq="Command 2", skillprereq={["Command"]=2}, desc="The character can spend 1 feat point to use Battle Plan: Go to Ground. Using a battle plan is a quick action. When a character uses this battle plan, each friendly character who follows the character's orders gains cover, does not suffer blast damage, and does not block LOS until he moves, is placed, or is engaged.", shortdesc=""},
	["Battle Plan: Reconnaissance"]={order=73, type="Ability", prereq="Command 2, Survival 2", skillprereq={["Command"]=2, ["Survival"]=3}, desc="The character can spend 1 feat point to use Battle Plan: Reconnaissance. Using a battle plan is a quick action. When a character uses this battle plan, the character and each friendly character who starts his activation in his command range gains the Pathfinder ability. Battle Plan: Reconnaissance lasts for one round.", shortdesc=""},
	["Battle Plan: Shadow"]={order=74, type="Ability", prereq="Command 1", skillprereq={["Command"]=1}, desc="The character can spend 1 feat point to use Battle Plan: Shadow. Using a battle plan is a quick action. When a character uses this battle plan, each friendly character who follows this character's orders gains Prowl (p. 165) for one round.", shortdesc=""},
	["Bayonet Charge"]={order=75, type="Ability", prereq="None", desc="When this character charges with a ranged weapon that has a bayonet, after moving but before making his charge attack, he can make one ranged attack targeting his charge target unless he was in melee with his charge target at the start of his turn. When resolving a Bayonet Charge ranged attack, the character does not suffer the target in melee attack roll penalty (see p. 212). If the target is not in melee range after the charging character moves, the character can make the Bayonet Charge ranged attack before his turn ends. A character making a Bayonet Charge must make his charge attack with a Bayonet.", shortdesc=""},
	["Big Game Hunter"]={order=76, type="Ability", prereq="Survival 1", skillprereq={["Survival"]=1}, desc="A character with the Big Game Hunter ability has hunted more than his fair share of game in the wilds of Immoren. When a character with Big Game Hunter makes a melee or ranged attack against a natural animal or beast native to the wilds of Immoren, he gains a bonus on attack rolls equal to his Survival skill.", shortdesc=""},
	["Binding"]={order=77, type="Ability", prereq="Rope Use 1", skillprereq={["Rope Use"]=1}, desc="When the character ties up, manacles, or otherwise restrains another character with some form of restraints, add +3 to the skill roll difficulty for the bound character to escape.", shortdesc=""},
	["Blaster"]={order=78, type="Ability", prereq="None", desc="The character gains +2 to hit with electrical ranged attacks.", shortdesc=""},
	["Blood Spiller"]={order=79, type="Ability", prereq="Hand Weapon 3", skillprereq={["Hand Weapon"]=3}, desc="The character gains +2 on damage rolls against living characters.", shortdesc=""},
	["Bodge"]={order=80, type="Ability", prereq="Mechanikal Engineering 1", skillprereq={["Mechanikal Engineering"]=1}, desc="The character can make temporary repairs to a steamjack in the midst of combat. See the \"Steamjacks\" chapter (p. 299) for details.", shortdesc=""},
	["Bodyguard"]={order=81, type="Ability", prereq="Shield Guard, Shield 3", abilityprereq={"Shield Guard"}, skillprereq={["Shield"]=3}, desc="This character is not limited in the number of times he can use Shield Guard each round.", shortdesc=""},
	["Bomber"]={order=82, type="Ability", prereq="Thrown Weapon 3", skillprereq={["Thrown Weapon"]=3}, desc="When this character's grenade ranged attack deviates, you can reroll the direction and/or distance of deviation. A roll can only be rerolled once as a result of Bomber.", shortdesc=""},
	["Bond"]={order=83, type="Ability", prereq="None", desc="This ability can be taken more than once. Each time a character takes this ability he gains a bonding slot that can be used to bond with warcaster armor, a steamjack, or a mechanika weapon - one per slot.\r\r    * The character must bond to a steamjack to make it part of his battlegroup.\r\r    * He must bond to a mechanika weapon to spend focus points to boost attack and damage rolls and to make additional attacks with it.\r\r    * He must bond to warcaster armor to gain the benefits of its power field and overboosting.\r\rTo form a bond, a character must spend a full action touching the steamjack or object he intends to bond to. Forming a bond with a steamjack requires a warcaster to know the cortex's native language and the pass codes to any locks on the cortex. A character can break a bond at will in order to free up a slot to form another bond. Bonded steamjacks are not affected by 'jack marshaling. A character cannot bond with a steamjack, mechanika weapon, or mechanika armor bonded to another character.", shortdesc="", multiability=true},
	["Brew Master"]={order=84, type="Ability", prereq="Alchemy 2", skillprereq={["Alchemy"]=2}, desc="The character can reroll failed Alchemy rolls. Each failed ro11 can be rerolled only once as a result of Brew Master.", shortdesc=""},
	["Camouflage"]={order=85, type="Ability", prereq="None", desc="The character gains an additional +2 DEF when benefiting from concealment or cover.", shortdesc=""},
	["Card Sharp"]={order=86, type="Ability", prereq="Gambling 2", skillprereq={["Gambling"]=2}, desc="The character is skilled at manipulating his fortune at cards by the judicious application of legerdemain. He's a cheat. When the character uses this ability, he adds an extra die to his Gambling skill rolls and drops the lowest die result.\r\rAnyone watching the character when he uses this ability can make a Detection skill roll to catch the character in the act.  The difficulty for this skill roll is equal to the cheating character's INT + AGL + Gambling skill.", shortdesc=""},
	["Cautious Advance"]={order=87, type="Ability", prereq="Survival 3", skillprereq={["Survival"]=3}, desc="The character gains one extra quick actions each turn that can be used only to take cover.", shortdesc=""},
	["Cavalry Charge"]={order=88, type="Ability", prereq="Riding 1", skillprereq={["Riding"]=1}, desc="The character can make a cavalry charge (p. 214) while riding a mount designated as a warhorse.", shortdesc=""},
	["Chain Attack: Bleed Out"]={order=89, type="Ability", prereq="Two-Weapon Fighting, Hand Weapon 3", abilityprereq={"Two-Weapon Fighting"}, skillprereq={["Hand Weapon"]=3}, desc="If this character fights with two melee hand weapons and hits the same living target with both his initial attacks, after resolving the attacks he can immediately make one additional melee attack against his target. If the additional attack hits, it does not inflict damage but the target must forfeit  either its movement or action on its next Activation Phase.", shortdesc=""},
	["Chain Attack: Pin Down"]={order=90, type="Ability", prereq="Two-Weapon Fighting, Pistol 3", abilityprereq={"Two-Weapon Fighting"}, skillprereq={["Pistol"]=3}, desc="If this character hits the same living target with initial attacks from two pistols, after resolving the attacks he can immediately make one additional ranged attack against that target ignoring ROF. If the additional attack hits, it does not inflict damage. Instead the target hit can advance up to 2\", and it is then knocked down.", shortdesc=""},
	["Choir"]={order=91, type="Ability", prereq="ARC 4", statprereq={["ARC"]=4}, desc="A character gains a cumulative +1 on magic attack rolls for each other friendly character of his faith with this ability within 1\" of him.", shortdesc=""},
	["Cleave"]={order=92, type="Ability", prereq="Great Weapon 1", skillprereq={["Great Weapon"]=1}, desc="When this character incapacitates one or more enemies with a melee attack made with a great weapon during his turn, the character can make one additional melee attack immediately after the attack is resolved. A character can gain only one additional attack from Cleave each turn.", shortdesc=""},
	["Combat Rider"]={order=93, type="Ability", prereq="Riding 1", skillprereq={["Riding"]=1}, desc="While this character is riding a mount designated as a warhorse, the mount can make one impact attack against a target in its melee range if the mount and rider did not charge this turn.", shortdesc=""},
	["Conniver"]={order=94, type="Ability", prereq="Bribery 1, Deception 1", skillprereq={["Bribery"]=1, ["Deception"]=1}, desc="The character is well skilled in the arts of deception. The character can reroll failed Bribery and Deception-based social skill rolls. Each roll can be rerolled only once as a result of Conniver.", shortdesc=""},
	["Cover Identity"]={order=95, type="Ability", prereq="Disguise 1", skillprereq={["Disguise"]=1}, desc="The character has an established cover identity with its own history, contacts, and criminal record (or lack thereof). The character's Disguise rolls while using his cover identity are automatically boosted. Choose a career for the cover identity. The character gains one occupational skill at level 1 from the cover identity's career. The cover identity must be reasonably believable. A gobber disguised as a trollkin fell caller won't fool anybody. This ability can be taken multiple times. Each time, it applies to a different cover identity.", shortdesc="", multiability=true },
	["Crackshot"]={order=96, type="Ability", prereq="None", desc="The character can accurately track his target despite its attempts to take cover. When making a ranged or magic attack against a target that has concealment, cover, or elevation, this character gains +2 on the attack ro11.", shortdesc=""},
	["Craft Rune Shot"]={order=97, type="Ability", prereq="None", desc="The character can craft his own rune shot ammunition. Instead of paying 5 gc for each metal cartridge round of rune shot ammunition, a character with this ability and a rune shot casting kit can cast his own rounds, paying 1 gc for the powder, material to cast a rune bullet, and metal casing to press one round of rune shot ammunition. The character must inscribe the casing and bullet by hand. A character can craft up to five rune shot cartridges in an hour.", shortdesc=""},
	["Crossbowman"]={order=98, type="Ability", prereq="None", desc="The character can reload a crossbow as a quick action instead of a full action.", shortdesc=""},
	["Defender"]={order=99, type="Ability", prereq="None", desc="Once per round, when a friendly character within this character's command range is hit with an enemy attack, immediately after the attack has been resolved this character can advance toward the enemy character, up to twelve feet (2\"), and make one melee attack.", shortdesc=""},
	["Defensive Line"]={order=100, type="Ability", prereq="None", desc="While this character is B2B with one or more friendly characters, he gains +1 ARM. While the character is B2B with one or more friendly characters who also have this ability, the bonus increases to +2.", shortdesc=""},
	["Dig In"]={order=101, type="Ability", prereq="None", desc="While he has a spade in hand, this character can make a quick action to dig an improvised foxhole. Until he moves, is placed, goes prone, or is engaged, the character gains cover, does not suffer blast damage, and does not block line of sight. A character cannot use the Dig In ability during a turn in which he ran.", shortdesc=""},
	["Disease Resistance"]={order=102, type="Ability", prereq="None", desc="The character can make boosted rolls to resist disease and infection.", shortdesc=""},
	["Dispel"]={order=103, type="Ability", prereq="None", desc="When this character hits an enemy with a melee attack, he can spend 1 feat point to cause any upkeep spells on that enemy to immediately expire.", shortdesc=""},
	["Dodger"]={order=104, type="Ability", prereq="None", desc="When this character is missed by an enemy attack, he can immediately advance up to 2\" after the attack is resolved unless was missed while advancing. He cannot be targeted by free strikes during this movement.", shortdesc=""},
	["Drive: Ancillary Attack"]={order=105, type="Ability", prereq="'Jack Marshal", abilityprereq={"'Jack Marshal"}, desc="This character can make a quick action to use Drive: Ancillary Attack on a steamjack he controls. The steamjack immediately makes one normal melee or ranged attack.", shortdesc=""},
	["Drive: Assault"]={order=106, type="Ability", prereq="'Jack Marshal", abilityprereq={"'Jack Marshal"}, desc="This character can make a quick action to use Drive: Assault on a steamjack he controls. The steamjack charges during its turn this round without spending focus or being forced. As part of that charge, after moving but before making its charge attack, the steamjack can make one ranged attack targeting its charge target unless it was in melee with its charge target at the start of its turn. When resolving a Drive: Assault ranged attack, the steamjack does not suffer the penalty for a target in melee. If the target is not in melee range after the steamjack moves, the steamjack can make the Drive: Assault ranged attack before its turn ends.", shortdesc=""},
	["Drive: Off Road"]={order=107, type="Ability", prereq="'Jack Marshal", abilityprereq={"'Jack Marshal"}, desc="This character can make a quick action to use Drive: Off Road on a steamjack he controls. The steamjack gains the Pathfinder ability during its turn this round and must use its normal movement and combat action to charge or make a slam power attack during its turn this round.", shortdesc=""},
	["Drive: Pronto"]={order=108, type="Ability", prereq="'Jack Marshal", abilityprereq={"'Jack Marshal"}, desc="This character can make a quick action to use Drive: Pronto on a steamjack he controls. The steamjack immediately makes a full advance.", shortdesc=""},
	["Dual Shot"]={order=109, type="Ability", prereq="None", desc="The character can forfeit his movement during his turn to make one additional ranged attack with a pistol or rifle.", shortdesc=""},
	["Elemental Mastery"]={order=110, type="Ability", prereq="ARC 5", statprereq={["ARC"]=5}, desc="The sorcerer gains +1 on his attack and damage rolls when casting an offensive spell from his elemental spell list.", shortdesc=""},
	["Empower"]={order=111, type="Ability", prereq="None", desc="During this character's turn, while he is B2B with a non-incapacitated friendly character, this character can spend 1 feat point to cause the friendly character to regain d3+1 vitality points.", shortdesc=""},
	["Expert Rider"]={order=112, type="Ability", prereq="Riding 2", skillprereq={["Riding"]=2}, desc="The character can reroll failed Riding rolls. Each roll can be rerolled only once as a result of Expert Rider.\r\rAdditionally, provided the mount has not been knocked out, this character and his mount cannot be knocked down while this character is mounted.", shortdesc=""},
	["Fast Cook"]={order=113, type="Ability", prereq="Alchemy 2", skillprereq={["Alchemy"]=2}, desc="The character has learned a number of time-saving shortcuts in the art of brewing potions and mixing alchemical substances. He can create alchemical items in half the normal time.", shortdesc=""},
	["Fast Draw"]={order=114, type="Ability", prereq="None", desc="A character with this ski1l gains +2 on initiative rolls. He also gains an additional quick action during his first turn of combat each encounter that can be used only to draw a weapon.", shortdesc=""},
	["Fast Rearm"]={order=115, type="Ability", prereq="None", desc="The character gains one extra quick action each turn that can be used only to rearm a weapon of the type noted, such as a blasting pike. A character can have this ability several times, each time with a different specified weapon.", shortdesc="", multiability=true },
	["Fast Reload"]={order=116, type="Ability", prereq="None", desc="The character gains one extra quick action each turn that can be used only to reload a ranged weapon.", shortdesc=""},
	["Fell Call: Cacophony"]={order=117, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="The character can use this call as a quick action. For one round, enemies cannot cast spells, use battle plans, or use drives while in this character's command range.", shortdesc=""},
	["Fell Call: Call of Defiance"]={order=118, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="The character can use this call as a quick action. For one round, when a friendly trollkin character makes a Tough ro11 while in this character's command range, on a roll of 4, 5, or 6 the trollkin heals 1 vitality point and is knocked down.", shortdesc=""},
	["Fell Call: Ground Shaker"]={order=119, type="Ability", prereq="Fell Calling 3", skillprereq={["Fell Calling"]=3}, desc="Once per turn, the character can use this call instead of attacking. The character then makes a RNG 10, AOE 5 fell call ranged attack that causes no damage. Targets hit are knocked down.", shortdesc=""},
	["Fell Call: Heroic Ballad"]={order=120, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="The character can use this call as a quick action. For the rest of the round, while in the Fell Caller's command range, friendly trollkin characters are fearless and gain +2 to melee attack rolls. Heroic Ballad lasts for one round.", shortdesc=""},
	["Fell Call: Reverberation"]={order=121, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="The character can use this call as a quick action. The character makes a RNG SP 6 ranged attack that causes no damage. The character uses his POI + Fell Calling skill for his attack rolls. Enemies hit are pushed d3\" directly away from this character. Move the enemy who was farthest away first.", shortdesc=""},
	["Fell Call: Signal Call"]={order=122, type="Ability", prereq="Fell Calling 1", skillprereq={["Fell Calling"]=1}, desc="The character can use this call as a quick action. The character can unleash a call that can be heard for a number of miles equal to his Fell Caller skill.", shortdesc=""},
	["Fell Call: Sonic Blast"]={order=123, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="Once per turn, the character can use this call instead of attacking. The character then makes a RNG SP 8, POW 12 ranged attack. The character uses his POI + Fell Calling skill for his attack rolls.", shortdesc=""},
	["Field Alchemist"]={order=124, type="Ability", prereq="Alchemy 1", skillprereq={["Alchemy"]=1}, desc="The character gains an additional quick action each turn that can be used only to create a field alchemy quick effect.", shortdesc=""},
	["Field Marshal: Magical Attack"]={order=125, type="Ability", prereq="ARC 5", statprereq={["ARC"]=5}, desc="The warcaster can spend a focus point during his turn to use Field Marshal: Magical Attack. For one round, the weapons of characters in his battlegroup become magical weapons while in the warcaster's control area.", shortdesc=""},
	["Field Marshal: Relentless Charge"]={order=126, type="Ability", prereq="ARC 7", statprereq={["ARC"]=7}, desc="The warcaster can spend a focus point during his turn to use Field Marshal: Relentless Charge. For one round, when a character in the warcaster's battlegroup begins a charge while in his control area, that character gains the Relentless Charge ability for his turn. (A character who already has Relentless Charge ignores penalties for rough terrain during an Activation Phase in which he charges.)", shortdesc=""},
	["Field Marshal: Shield Guard"]={order=127, type="Ability", prereq="ARC 7", statprereq={["ARC"]=7}, desc="The warcaster can spend a focus point during his turn to use Field Marshal: Shield Guard. For one round, characters in his battlegroup gain the Shield Guard ability while in the warcaster's control area. Once per turn, when a friendly character is directly hit by an attack while within 2\" of a character with Shield Guard, the character with Shield Guard can become the target of the attack and be automatically hit instead. A character cannot use Shield Guard if he is incorporeal, knocked down, or stationary.", shortdesc=""},
	["Find Cover"]={order=128, type="Ability", prereq="None", desc="At the start of combat before initiative is rolled, this character can immediately advance up to twelve feet (2\") and perform a quick action to take cover or go prone.", shortdesc=""},
	["Fire in the Hole!"]={order=129, type="Ability", prereq="Thrown Weapon 1", skillprereq={["Thrown Weapon"]=1}, desc="This character can make a grenade attack at the start of the Action Phase of his turn before moving or making his normal attacks. A character making a Fire in the Hole! attack must use his movement that turn to run or make a full advance.", shortdesc=""},
	["Fleet Foot"]={order=130, type="Ability", prereq="SPD 7", statprereq={["SPD"]=7}, desc="When the character runs, he moves at SPD x 3.", shortdesc=""},
	["Free Style"]={order=131, type="Ability", prereq="Alchemy 1", skillprereq={["Alchemy"]=1}, desc="The character can improvise the ingredients in his alchemical compounds. This allows the character to attempt to make do without a specific ingredient. This requires an Alchemy skill roll with a target number equal to 10 plus the gc value of the ingredient.\r\rThis ability also allows the character to get by with less expensive versions of common alchemical ingredients, reducing the cost of his alchemical compounds by 1 gc each (to a minimum of 1 gc). Alchemical compounds brewed using this skill vary slightly in appearance or physical quality from items created by following time-tested recipes.", shortdesc=""},
	["Gang"]={order=132, type="Ability", prereq="None", desc="When making a melee attack that targets an enemy in melee range of another friendly character, this character gains +1 to melee attack and melee damage rolls. When making a melee attack that targets an enemy in melee range of another friendly character who also has this ability, these bonuses increase to +2.", shortdesc=""},
	["Get Away"]={order=133, type="Ability", prereq="Dodger, Escape Artist 3", abilityprereq={"Dodger"}, skillprereq={["Escape Artist"]=3}, desc="When the character is missed by an enemy attack at any time other than while advancing, instead of advancing up to 2\", the character can immediately. make a fu1l advance.", shortdesc=""},
	["Girded"]={order=134, type="Ability", prereq="Shield 2", skillprereq={["Shield"]=2}, desc="While armed with a shield, this character does not suffer blast damage. Friendly characters B2B with this character do not suffer blast damage either.", shortdesc=""},
	["Good Breeding"]={order=135, type="Ability", prereq="None", desc="This character was raised in high society. He can reroll failed Etiquette rolls. Each roll can be rerolled only once as a result of Good Breeding.", shortdesc=""},
	["Great Power"]={order=136, type="Ability", prereq="None", desc="This character can upkeep one spell each turn without spending a focus point or gaining a fatigue point.", shortdesc=""},
	["Grenadier"]={order=137, type="Ability", prereq="Thrown Weapon 1", skillprereq={["Thrown Weapon"]=1}, desc="The character gains an additional quick action each turn that can be used only to pull the pin on a grenade.", shortdesc=""},
	["Gunfighter"]={order=138, type="Ability", prereq="None", desc="The character does not suffer a -4 penalty on ranged attack rolls with pistols or carbines while engaged.", shortdesc=""},
	["Head-Butt"]={order=139, type="Ability", prereq="STR 5, Unarmed Combat 2", statprereq={["STR"]=5}, skillprereq={["Unarmed Combat"]=2}, desc="Instead of making a normal Unarmed Combat attack, this character can spend a feat point to make a head-butt attack. The character makes an unarmed melee attack ro11 against his target. If the attack hits, the target is knocked down and suffers a damage roll with a POW (Power) equal to the character's current STR. A character cannot head-butt a target with a larger base.", shortdesc=""},
	["Hit the Deck!"]={order=140, type="Ability", prereq="None", desc="The character is so accustomed to catastrophic explosions in his presence that he has developed the uncanny ability to hit the ground the second before he is affected by a blast. While prone, the character does not suffer damage from AOEs unless he is directly hit by the AOE. If the character is caught in an AOE that would cause blast damage but was not directly hit bv the AOE, he goes prone but suffers no damage.", shortdesc=""},
	["Hyper Awareness"]={order=141, type="Ability", prereq="Command 3", skillprereq={["Command"]=3}, desc="While in this character's command range, friendly characters gain Circular Vision. (The front arc of a character with Circular Vision extends to 360 degrees.)", shortdesc=""},
	["Immunity: Cold"]={order=142, type="Ability", prereq="None", desc="The character is immune to cold damage.", shortdesc=""},
	["Immunity: Corrosion"]={order=143, type="Ability", prereq="None", desc="The character is immune to corrosion damage.", shortdesc=""},
	["Immunity: Electricity"]={order=144, type="Ability", prereq="None", desc="The character is immune to electrical damage.", shortdesc=""},
	["Immunity: Fire"]={order=145, type="Ability", prereq="None", desc="The character is immune to fire damage.", shortdesc=""},
	["Inscribe Formulae"]={order=146, type="Ability", prereq="Mechanikal Engineering 1", skillprereq={["Mechanikal Engineering"]=1}, desc="The character can inscribe runeplates. See \"Mechanika\" (p. 279).", shortdesc=""},
	["Iron Sentinel"]={order=147, type="Ability", prereq="'Jack Marshal, Command 3", abilityprereq={"'Jack Marshal"}, skillprereq={["Command"]=3}, desc="The character has learned to position himself behind the steamjacks he commands to stay out of harm's way. While B2B with one or more steamjacks he commands, the character gains +2 ARM and cannot be knocked down.", shortdesc=""},
	["Iron Will"]={order=148, type="Ability", prereq="None", desc="The character can reroll failed Willpower rolls. Each roll can be rerolled only once as a result of Iron Will.", shortdesc=""},
	["Keen Eyed"]={order=149, type="Ability", prereq="None", desc="The character can increase his effective range with a bow or rifle by twelve feet (2\") and his extreme range by sixty feet (10\").", shortdesc=""},
	["Language"]={order=150, type="Ability", prereq="None", desc="This ability can be taken multiple times. Each time a character takes this ability, he learns how to speak, read, and write a new language.", shortdesc="", multiability=true },
	["Legacy of Bragg"]={order=151, type="Ability", prereq="Fell Calling 2", skillprereq={["Fell Calling"]=2}, desc="The character gains an additional quick action each turn that can be used only to make fell calls.", shortdesc=""},
	["Light Cavalry"]={order=152, type="Ability", prereq="Riding 2", skillprereq={["Riding"]=2}, desc="If this character is riding a mount not designated as a warhorse, at the end of his turn he can advance up to 5\".", shortdesc=""},
	["Load Bearing"]={order=153, type="Ability", prereq="STR 5", statprereq={["STR"]=5}, desc="The character is well practiced at fighting while wearing heavy armor. Reduce the SPD and DEF penalties from the armor the character wears each by 1.", shortdesc=""},
	["Mage Killer"]={order=154, type="Ability", prereq="PER 6", statprereq={["PER"]=6}, desc="This character gains an additional die on damage rolls against characters that can cast spells.", shortdesc=""},
	["Marksman"]={order=155, type="Ability", prereq="None", desc="The character can use quick actions and aim in the same round, but still has to forfeit his movement to gain the aiming bonus.", shortdesc=""},
	["Natural Leader"]={order=156, type="Ability", prereq="Command 1", skillprereq={["Command"]=1}, desc="A character with Natural Leader increases his command range by 2\".", shortdesc=""},
	["Night Fighter"]={order=157, type="Ability", prereq="None", desc="The character is well versed in dispatching an opponent at a distance in the dark of night. When making a ranged or magic attack against a character with stealth or Prowl, this character automatically misses the target only if he is 8\" or farther away (instead of 5\" away).", shortdesc=""},
	["Parry"]={order=158, type="Ability", prereq="None", desc="While armed with a hand weapon, the character cannot be targeted by free strikes.", shortdesc=""},
	["Pathfinder"]={order=159, type="Ability", prereq="Survival 1", skillprereq={["Survival"]=1}, desc="The character can move over rough terrain without penalty.", shortdesc=""},
	["Poison Resistance"]={order=160, type="Ability", prereq="None", desc="The character gains boosted attack rolls to resist poisons and toxins.", shortdesc=""},
	["Port of Call"]={order=161, type="Ability", prereq="Navigation 1", skillprereq={["Navigation"]=1}, desc="A character with Port of Call can always find his way home again. For each level of Navigation, a character can nominate one location he's visited more than once as a Port of Call. When traveling to a Port of Call and using the Navigation skill (p. 186), treat any results worse than \"Arrive as planned\" as \"Arrive as planned.\"", shortdesc=""},
	["Precision Strike"]={order=162, type="Ability", prereq="None", desc="When the character hits with a melee attack, he chooses the branch of the target's life spiral or the column of the target's damage grid that is hit, if applicable.", shortdesc=""},
	["Press the Attack"]={order=163, type="Ability", prereq="Shield Slam, Shield 3", abilityprereq={"Shield Slam"}, skillprereq={["Shield"]=3}, desc="When this character slams an enemy, immediately after the slam is resolved this character can advance directly toward the slammed enemy up to the distance the slammed enemy was moved and then make a melee attack against that enemy.", shortdesc=""},
	["Privilege"]={order=164, type="Ability", prereq="None", desc="Whether he was born into an ancient bloodline or a family that purchased status, this character is afforded the rights of a noble. He is immune to persecution for petty crimes and can be tried only by a court of his peers. Should he be found guilty of a high crime, he has the right to be executed in the style befitting a noble. Each kingdom has distinct benefits and obligations for those born of privilege. With a successful Etiquette skill roll, this character can demand hospitality and request aid from other nobles not at war with his kingdom. Additionally, the character gains +2 on social skill rolls when dealing with those beneath his station who recognize his status and respect the nobility.", shortdesc=""},
	["Prowl"]={order=165, type="Ability", prereq="Sneak 1", skillprereq={["Sneak"]=1}, desc="The character is virtually invisible while in the shadons or in terrain that grants a degree of concealment. The character gains stealth (p.220) while within terrain that provides concealment, the AOE of a spell that provides concealment, or the AOE of a cloud effect.", shortdesc=""},
	["Pursuit"]={order=166, type="Ability", prereq="Tracking 3", skillprereq={["Tracking"]=3}, desc="The character can spend 1 feat point during his turn to use this ability. When this ability is used, the character designates one enemy. For the rest of the encounter, when that enemy moves during his turn, immediately after the enemy ends his movement this character can make a full advance. A character can use this benefit only once per encounter unless the original subject of this abilitv is destroyed, at which point the character can spend a feat point to use this benefit on a new enemy.", shortdesc=""},
	["Quick Work"]={order=167, type="Ability", prereq="AGL 5", statprereq={["AGL"]=5}, desc="When this character kills one or more enemies with a melee attack during his combat action, immediatelv after that attack is resolved this character can make one ranged attack.", shortdesc=""},
	["Rallying Cry"]={order=168, type="Ability", prereq="Command 3", skillprereq={["Command"]=3}, desc="This character can spend a feat point to use Rallying Cry. For one round, this character's command range is doubled and friendly characters in his command range, including this character, become fearless (p.221).", shortdesc=""},
	["Relentless Charge"]={order=169, type="Ability", prereq="None", desc="This character ignores penalties for rough terrain while charging.", shortdesc=""},
	["Resourceful"]={order=170, type="Ability", prereq="Mechanikal Engineering 3", skillprereq={["Mechanikal Engineering"]=3}, desc="This character can upkeep spells on steamjacks he controls without spending focus or gaining fatigue points.", shortdesc=""},
	["Retaliatory Strike"]={order=171, type="Ability", prereq="PHY 7", statprereq={["PHY"]=7}, desc="When this character is hit by a melee attack made by an enemy at any time other than during his own turn, after the attack is resolved the character can spend 1 feat point to immediately make one normal melee attack against the enemy that hit him.", shortdesc=""},
	["Return Fire"]={order=172, type="Ability", prereq="Fast Draw", abilityprereq={"Fast Draw"}, desc="Once per round when this character is missed by an enemy's ranged attack, immediately after the attack is resolved he can make one normal attack against the attacking enemy.\r\rTo make a ranged attack, the character's ranged weapon must be loaded.", shortdesc=""},
	["Ride-By Attack"]={order=173, type="Ability", prereq="Riding 2", skillprereq={["Riding"]=2}, desc="While mounted, this character can combine his movement and action during his turn to make a Ride-By Attack. The character declares a Ride-By Attack at the start of his Activation Phase. He makes a full advance and can halt his movement at any point to make his attacks. After his attacks, he resumes his movement.", shortdesc=""},
	["Riposte"]={order=174, type="Ability", prereq="None", desc="Once per round when this character is missed by an enemy's melee attack, immediately after the attack is resolved he can make one normal attack against the attacking enemy.\r\rTo make a ranged attack, the character's ranged weapon must b e loaded.", shortdesc=""},
	["Rock Solid"]={order=175, type="Ability", prereq="PHY 8", statprereq={["PHY"]=8}, desc="This character and friendly characters B2B with him cannot be knocked down. The character loses this ability while he is mounted.", shortdesc=""},
	["Roll With It"]={order=176, type="Ability", prereq="None", desc="When the character would ordinarily be knocked down, he goes prone instead. The character loses this ability while he is mounted.", shortdesc=""},
	["Saddle Shot"]={order=177, type="Ability", prereq="Riding 1", skillprereq={["Riding"]=1}, desc="This character does not suffer the firing from horseback penalty when making ranged attacks while mounted (see p. 214).", shortdesc=""},
	["Scrounge"]={order=178, type="Ability", prereq="None", desc="The character is adept at finding hard to acquire or expensive replacement parts to repair steamjacks. Given a day to find the parts in a town or industrialized settlement, the character can find any part needed (or something close enough he can bodge it together). The character can purchase steamjack parts, weapons, and gear for half the normal price.", shortdesc=""},
	["Sentry"]={order=179, type="Ability", prereq="None", desc="Once per round when an enemy is placed in or moves into line of sight of this character, this character can immediately make one attack, targeting that enemy.", shortdesc=""},
	["Set Defense"]={order=180, type="Ability", prereq="Great Weapon 2", skillprereq={["Great Weapon"]=2}, desc="While this character is armed with a weapon that has reach, an enemy in this character's front arc suffers -2 on charge, slam power attack, and impact attack rolls against him.", shortdesc=""},
	["Shadow Magic"]={order=181, type="Ability", prereq="Gifted, Sneak 2", archetypeprereq={"Gifted"}, skillprereq={["Sneak"]=2}, desc="When this character casts a spell, no spell runes appear and magically sensitive characters cannot sense the character's magic. A focuser with this ability cannot be detected by another focuser with the Magic Sensitivity archetype benefit.", shortdesc=""},
	["Shield Guard"]={order=182, type="Ability", prereq="Shield 1", skillprereq={["Shield"]=1}, desc="Once per turn, when a friendly character is directly hit by an attack while within 2\" of this character, this character can choose to be directly hit instead. This character cannot use Shield Guard if he is incorporeal, knocked down, prone, or stationary.", shortdesc=""},
	["Shield Slam"]={order=183, type="Ability", prereq="STR 6", statprereq={["STR"]=6}, desc="When this character charges while armed with a shield, instead of making a charge attack, he can spend a feat point to slam his target, making an attack roll with his shield. A charater who slams another character with a larger base suffers -2 to this attack roll. The POW of the slam damage roll is equal to the STR of the attacking character plus the POW of the shield. The POW of collateral damage is equal to the STR of the attacking character.", shortdesc=""},
	["Signal Language"]={order=184, type="Ability", prereq="Cryptography 1", skillprereq={["Cryptography"]=1}, desc="The character can spend time developing a coded signal language for use with his party using the same rules as creating a code. The language can convey simple messages or commands and has a Cryptography target number basedo n the target number used during the code's creation. The signal language can be taught to any character that has Intellect 3 or greater.", shortdesc=""},
	["Sniper"]={order=185, type="Ability", prereq="Rifle 3", skillprereq={["Rifle"]=3}, desc="When this character forfeits his movement to aim during his turn, the character's first ranged attack that turn gains boosted damage.", shortdesc=""},
	["Specialization"]={order=186, type="Ability", prereq="None", desc="The character does not suffer attack roll penalties when attacking with a weapon of the type chosen, such as a blasting pike. A character can have this ability several times, each time choosing a different weapon.", shortdesc="", multiability=true },
	["Steady"]={order=187, type="Ability", prereq="None", desc="This character cannot be knocked down. The character loses this ability while he is mounted.", shortdesc=""},
	["Steamo"]={order=188, type="Ability", prereq="Mechanikal Engineering 2", skillprereq={["Mechanikal Engineering"]=2}, desc="The character can reroll failed Mechanikal Engineering rolls to repair or dismantle steamjacks. Each roll can be rerolled only once as a result of Steamo.", shortdesc=""},
	["Sucker!"]={order=189, type="Ability", prereq="Intimidation 3", skillprereq={["Intimidation"]=3}, desc="If this character is directly hit by an enemy ranged attack, he can choose a friendly living non-incorporeal character within 2\" of him to be directly hit instead. That character is automatically hit and suffers all damage and effects.", shortdesc=""},
	["Swift Hunter"]={order=190, type="Ability", prereq="AGL 6", statprereq={["AGL"]=6}, desc="When this character incapacitates an enemy by using a normal ranged attack, immediately after the attack is resolved he can advance up to twelve feet (2\").", shortdesc=""},
	["Swift Rider"]={order=191, type="Ability", prereq="None", desc="While riding a mount, the character can move over rough terrain without penalty", shortdesc=""},
	["Take Down"]={order=192, type="Ability", prereq="None", desc="This character can use Take Down anytime he incapacitates another character with an attack and while the incapacitated character is in this character's melee range. The incapacitated character regains 1 vitality point and is no longer incapacitated but is considered to be manacled, tied up, unconscious, or otherwise out of action for the rest of the encounter. Once the combat portion of the encounter has ended, the subject of the take down is at the mercy of the victors to be questioned or worse.", shortdesc=""},
	["Targeteer"]={order=193, type="Ability", prereq="None", desc="When the character hits with a ranged attack, he chooses the branch of the target's life spiral or the column of the target's damage grid that is hit, if applicable.", shortdesc=""},
	["Team Leader"]={order=194, type="Ability", prereq="None", desc="When this character gains a feat point, instead of keeping it himself he can give it to another character currently in his command range.", shortdesc=""},
	["Traceless Path"]={order=195, type="Ability", prereq="Sneak 2", skillprereq={["Sneak"]=2}, desc="The character knows how to conceal his trail when moving over land. Though he can move at only half his usual rate of speed while using this abilitly, either on foot or horseback, anyone attempting to follow his trail has +3 added to his skill roll target number.", shortdesc=""},
	["Truth Reader"]={order=196, type="Ability", prereq="Detection 3", skillprereq={["Detection"]=3}, desc="The character automatically knows when someone is lying to him. Keep in mind that knowing someone is lying is different than discerning the truth.", shortdesc=""},
	["Tune Up"]={order=197, type="Ability", prereq="Mechanikal Engineering 3", skillprereq={["Mechanikal Engineering"]=3}, desc="While B2B with a steamjack he controls, this character can spend a quick action to use Tune Up. During this round, the steamjack gains either boosted attack or damage rolls.", shortdesc=""},
	["Two-Weapon Fighting"]={order=198, type="Ability", prereq="AGL 4", statprereq={["AGL"]=4}, desc="While fighting with a one-handed weapon or pistol in each hand, the character gains an additional attack for the second weapon. He suffers -2 on attack rolls with the second weapon while doing so.", shortdesc=""},
	["University Education"]={order=199, type="Ability", prereq="None", desc="The character can reroll failed Lore and Research skill rolls. Each roll can be rerolled only once as a result of University Education.", shortdesc=""},
	["Waylay"]={order=200, type="Ability", prereq="None", desc="When an attack made by this character has the chance to knock out a target, increase the target number for the Willpower roll to resist the knockout by 2.", shortdesc=""},
	};

