spelldescriptions={
	["Arcane Bolt"] = { order=1, cost=2, range=12, aoe="-", power=11, upkeep="No", offensive="Yes", 
								description="Magical bolts of energy streak toward the target."},
	["Arcane Strike"] = {order=2,  cost=1, range=8, aoe="-", power=8, upkeep="No", offensive="Yes", 
								description="An arcane force blasts toward the target."},
	["Arcantrik Bolt"] = {order=3,  cost=2, range=10, aoe="-", power=12, upkeep="No", offensive="Yes",
								description="A steamjack damaged by this attack becomes stationary for one round." },
	["Ashen Cloud"]	=	{order=4,  cost=2, range="CTRL", aoe=3, power="-", upkeep="Yes", offensive="No",
								description="Place a 3\" AOE cloud effect anywhere completely in the spellcaster's control area. Characters without Immunity: Fire suffer -2 on attack rolls while within the AOE."},
	["Ashes to Ashes"] = {order=5,  cost=4, range=8, aoe="*", power=10, upkeep="No", offensive="Yes",
								description="If target character is hit, he and d6 of the nearest enemies within 5\" of the target suffer a POW 10 fire damage roll." },
	["Aura of Protection"] = {order=6,  cost=2, range="Self", aoe="CTRL", power="-", upkeep="Yes", offensive="No",
								description="While in the spellcaster's control area, friendly characters gain +2 ARM."},
	["Awareness"]={order=7, cost= 3, range="Self", aoe="CTRL", power="-", upkeep="No", offensive="No",
								description="While in the spellcaster's control area, the front arcs of characters in his battlegroup are extended to 360°. When determining LOS, those characters ignore cloud effects, forests, and intervening characters. Awarenes lasts for one round."},
	["Banishing Ward"]= {order=8, cost=2, range=6, aoe="-", power="-", upkeep="Yes", offensive="No",
								description="Enemy upkeep spells on the targeted friendly character expire. The affected character cannot be targeted by enemy spells or animi."},
	["Barrier of Flames"] = {order=9, cost=3, range="Self", aoe="CTRL", power="-", upkeep="No", offensive="No",
								description="Friendly characters in the spellcaster's control area gain +1 DEF. When a friendly character is hit by a melee attack while in the spellcaster's control area, the attacker suffers the Fire continuous effect. Barrier of Flames lasts for one round."},
	["Battering Ram"] = {order=10, cost=2, range=6, aoe="-", power=12, upkeep="No", offensive="Yes",
								description="When a character is hit by Battering Ram, he can be pushed 3\" directly away from the spell's point of origin."},
	["Batten Down the Hatches"] = {order=11, cost=3, range="Self", aoe="CTRL", power="-", upkeep="No", offensive="No",
								description="While in the spellcaster's control area, characters in his battlegroup cannot be knocked down and gain +3 ARM but suffer -2 DEF. Batten Down the Hatches lasts for one round."},
	["Black Out"] = {order=12, cost=3, range="Self", aoe="CTRL", power="-", upkeep="No", offensive="No",
								description="Mechanika devices in the possession of enemy characters in the spellcaster's control area immediately deactivate. If an enemy enters the spellcaster's control area, mechanika devices in his possession immediately deactivate. While in the spellcaster's control area, enemy characters cannot activate mechanika devices. Black Out has no effect on steamjacks or mechanika armor. Black Out lasts for one round."},
	["Blade of Radiance"] = {order=13, cost=2, range=10, aoe="-", power=10, upkeep="No", offensive="No",
								description="Infernal and Undead characters hit by this spell suffer an additional die of damage."},
	["Blazing Effigy"] = {order=14, cost=4, range="Self", aoe="*", power=14, upkeep="No", offensive="No",
								description="Enemies within 2\" of the targeted friendly character suffer a POW 14 fire damage roll."},
	["Blessing of Health"] = {order=15, cost=1, range=6, aoe="-", power="-", upkeep="Yes", offensive="No",
								description="Target character gains +3 on PHY rolls to resist poison, disease, and infection. Additionally, if the affected character is currently suffering from the effects of a poison, he immediately makes a PHY roll against the toxin rating of the poison. If the roll succeeds, the effects of the poison immediately expire."},
	["Blessing of Morrow"] = {order=16, cost=3, range="Self", aoe="CTRL", power="-", upkeep="Yes", offensive="No",
								description="While in the spellcaster's control area, friendly living characters do not suffer the effects of lost aspects."},
	["Blessings of War"] = {order=17, cost=2, range=6, aoe="-", power="-", upkeep="Yes", offensive="No",
								description="Target haracter's weapons gains Blessed.(When making an attack with a weapon with Blessed, ignore spell effects that add to the attacking character's ARM or DEF.)"},
	["Blizzard"] = {order=18, cost=1, range=6, aoe="-", power="-", upkeep="No", offensive="No",
								description="Center a 3\" AOE cloud effect on tharget character. The AOE remains centered on the character. If the target character is destroyed, remove the AOE from play. Blizzard lasts for one round."},
	["Brittle Frost"] = {order=19, cost=3, range=8, aoe="-", power="-", upkeep="Yes", offensive="Yes",
								description="The next time target enemy suffers damage, have its base ARM when calculating damage from the damage roll. After applying this damage, Brittle Frost expires."},
	["Boundless Charge"] = {order=20, cost=2, range=6, aoe="-", power="-", upkeep="No", offensive="No",
								description="During his turn, target character can charge without spending focus or being forced and gains +2\" movement and Pathfinder when it charges. Boundless Charge lasts for one round."},
	["Broadside"] = {order=21, cost=3, range="Self", aoe="CTRL", power="-", upkeep="No", offensive="No",
								description="The spellcaster and steamjacks under the spellcaster's control currently in his control area can immediately make one normal ranged attack. Broadside can be case only once per turn."},
	["Celerity"] = {order=22, cost=2, range=6, aoe="-", power="-", upkeep="Yes", offensive="No",
								description="Target character gains one additional quick action during each f his turns."},
	["Chain Lightning"] = {order=23, cost=3, range=10, aoe="-", power=10, upkeep="No", offensive="Yes",
								description="A character hit by Chain Lightning suffers POW 10 electrical damage roll, and lightning arcs from that character to d6 consecutive additional characters. The lightning arcs to the nearest character it has not already arced to withing 4\" of the last model it arced to, ignoring the spellcaster. Each character the lightning arcs to suffers a POW 10 electrical damage roll."},
